        el => {
          // Try to find text like "You and 1 other", "1 Like", etc.
          const txt = el.innerText || '';
          const match = txt.match(/(\d+)\s+Like/);
          if (match) return match[0];
          if (/You\s+like\s+this/i.test(txt)) return 'You like this';
          return txt;
