import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // --- AUTOMATION LOGIC STARTS HERE ---

    // Go to home page after login (simulate login if needed)
    // Try to use localStorage to set fbDemoUser and simulate login redirect
    await page.evaluate(() => {
      const userData = {
        id: 'john',
        email: 'demo@fake.com',
        name: 'john',
        loggedIn: true,
        avatar: 'https://via.placeholder.com/40/1877f2/ffffff?text=J',
        loginTime: new Date().toISOString()
      };
      localStorage.setItem('fbDemoUser', JSON.stringify(userData));
    });

    // Go to the home.html page (simulate what Facebook would do after login)
    const homeUrl = fileUrl.replace(/\/[^\/]*$/, '/home.html');
    await page.goto(homeUrl);
    await page.waitForLoadState('networkidle');

    // Wait for the first post to appear; try multiple selectors
    // Try to find a post by .post, .feed, or something similar
    let postSelector = null;
    let postHandle = null;
    // Try common post selectors
    const possibleSelectors = [
      '.post', // generic post class
      '[data-testid="post-container"]',
      '.feed .card',
      '.feed .post',
      '.card', // fallback
      'article', // fallback
      'div:has-text("Like")' // look for a div containing a Like button
    ];
    for (const sel of possibleSelectors) {
      try {
        await page.waitForSelector(sel, { timeout: 2000 });
        postSelector = sel;
        postHandle = await page.$(sel);
        if (postHandle) break;
      } catch (e) {
        // Try next selector
      }
    }
    if (!postHandle) {
      throw new Error('Could not locate the first post on home.html');
    }

    // Try to find the reactions area or Like button within the post
    // Look for a button or span containing "Like" or "React"
    let reactionButton = null;
    // Try several selector strategies inside the post
    const reactionCandidates = [
      'button:has-text("Like")',
      'button:has-text("React")',
      'span:has-text("Like")',
      '.reactions button',
      '.reactions .fake-react-btn',
      '.like-btn',
      '.react-btn'
    ];
    for (const rc of reactionCandidates) {
      try {
        reactionButton = await postHandle.$(rc);
        if (reactionButton) break;
      } catch (e) {}
    }

    // If can't find inside post, try on page level
    if (!reactionButton) {
      for (const rc of reactionCandidates) {
        try {
          reactionButton = await page.$(rc);
          if (reactionButton) break;
        } catch (e) {}
      }
    }

    if (!reactionButton) {
      // Try Xpath as last resort, e.g. button with text "Like"
      const [likeBtn] = await page.$x("//button[contains(., 'Like') or contains(., 'React')]");
      if (likeBtn) {
        reactionButton = likeBtn;
      }
    }

    if (!reactionButton) {
      throw new Error('Could not locate a Like/React button in the first post');
    }

    // Click the reaction button (simulate fake reaction)
    await reactionButton.click();
    // Wait briefly for reaction UI to update (simulate)
    await page.waitForTimeout(800);

    // Optionally, check if the reaction count or icon updated
    // Look for a reaction counter or visual change
    let reactionText = '';
    let reactionCount = null;
    // Try to get updated reaction info from the post
    const reactionInfoSelectors = [
      '.reactions', '.reaction-count', '.like-count', 'span:has-text("You and")', 'span:has-text("liked this")'
    ];
    for (const ri of reactionInfoSelectors) {
      try {
        const handle = await postHandle.$(ri);
        if (handle) {
          reactionText = await handle.innerText();
          // Try to extract a number
          const match = reactionText.match(/\d+/);
          reactionCount = match ? parseInt(match[0], 10) : null;
          break;
        }
      } catch (e) {}
    }

    // Take screenshot after reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_reaction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_reaction.png'));

    // --- END AUTOMATION LOGIC ---

    return {
      success: true,
      action: "Added a fake reaction to the first post on the Facebook demo homepage using alternative selector strategies.",
      extractedData: {
        postSelector,
        reactionText,
        reactionCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
