import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Since there's no actual "home" or "post" on the login page,
    // let's check if we're already on a feed-like page (home.html), else try to login with demo credentials to get there

    // Try to detect if this is the login page by checking for the login form
    const loginFormExists = await page.$('form#loginForm');
    if (loginFormExists) {
      // Fill in the login form with demo credentials and log in
      await page.waitForSelector('#email');
      await page.fill('#email', 'demouser@example.com');
      await page.fill('#password', 'demopass123');
      // Take screenshot before login
      const step2 = path.join(screenshotsDir, 'step_2_login_filled.png');
      await page.screenshot({ path: step2 });
      screenshots.push(step2);

      await Promise.all([
        page.click('button.login-btn'),
        page.waitForNavigation({ waitUntil: 'networkidle', timeout: 5000 })
      ]);
    }

    // Now on home.html (feed page), wait for main feed content to load
    // Try various selectors for posts. We'll use text, role, and structure selectors

    // Wait for a post element (look for common Facebook post structures)
    // Try multiple selectors for robustness
    let postSelector = null;
    let postElement = null;
    const possibleSelectors = [
      '[data-testid="fb-post"]', // custom attribute if exists
      'article',                 // semantic tag often used for posts
      '.post',                   // class "post"
      '[class*="post"]',         // partial class match
      'div:has-text("Like")',    // div containing Like button text
      'div:has(button:has-text("Like"))',
      'section:has(button:has-text("Like"))'
    ];
    for (const sel of possibleSelectors) {
      postElement = await page.$(sel);
      if (postElement) {
        postSelector = sel;
        break;
      }
    }

    // If not found, try to fallback to a visible Like button as base
    if (!postElement) {
      // Find first visible "Like" button
      await page.waitForSelector('button:has-text("Like")', { timeout: 4000 });
      const likeButtons = await page.$$('button:has-text("Like")');
      if (likeButtons.length > 0) {
        // Go up to parent post container
        postElement = await likeButtons[0].evaluateHandle(btn => btn.closest('article, .post, .fb-post, section, div'));
        postSelector = 'button:has-text("Like") (parent)';
      }
    }

    // Take screenshot of found post (before reaction)
    const step3 = path.join(screenshotsDir, 'step_3_first_post_detected.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Now, try to add a fake reaction to the post
    // Look for "Like" button inside the post, click it
    let likeButton = null;
    if (postElement) {
      // Try to find Like button inside post
      likeButton = await postElement.$('button:has-text("Like")');
      if (!likeButton) {
        // Try alternative selectors inside post
        const altBtns = await postElement.$$('button');
        for (const btn of altBtns) {
          const btnText = await btn.textContent();
          if (btnText && btnText.trim().toLowerCase().includes('like')) {
            likeButton = btn;
            break;
          }
        }
      }
    } else {
      // Fallback: try to find first Like button in DOM
      likeButton = await page.$('button:has-text("Like")');
    }

    let reactionSuccess = false;
    if (likeButton) {
      await likeButton.click();
      reactionSuccess = true;
    }

    // Optionally, try to add more fake reactions (e.g., "Love", "Haha") if such buttons exist nearby
    let otherReactions = [];
    if (postElement) {
      const extraReactions = await postElement.$$('button');
      for (const btn of extraReactions) {
        const btnText = await btn.textContent();
        if (btnText && ['love', 'haha', 'wow', 'sad', 'angry'].some(r => btnText.trim().toLowerCase().includes(r))) {
          await btn.click();
          otherReactions.push(btnText.trim());
        }
      }
    }

    // Take screenshot after reactions
    const step4 = path.join(screenshotsDir, 'step_4_fake_reactions_added.png');
    await page.screenshot({ path: step4 });
    screenshots.push(step4);

    // Extract data: What reactions are now visible in the post (if any counter or label updated)
    let extractedReactions = [];
    if (postElement) {
      const reactionSpans = await postElement.$$('span');
      for (const span of reactionSpans) {
        const text = await span.textContent();
        if (text && ['Like', 'Liked', 'Love', 'Haha', 'Wow', 'Sad', 'Angry'].some(r => text.includes(r))) {
          extractedReactions.push(text.trim());
        }
      }
    }

    return {
      success: reactionSuccess,
      action: reactionSuccess
        ? "Added fake reactions to first post using multiple selectors and robust strategies"
        : "Failed to find or react to a post",
      extractedData: {
        postSelector,
        reactionsAttempted: ['Like'].concat(otherReactions),
        reactionsFound: extractedReactions
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
