import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // --- LOGIN FLOW ---
    // Wait for login form fields
    await page.waitForSelector('#email');
    await page.waitForSelector('#password');
    await page.waitForSelector('button.login-btn');

    // Fill in demo credentials
    await page.fill('#email', 'scheduledemo@example.com');
    await page.fill('#password', 'password123');

    // Screenshot before login
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_login.png'));

    // Click login
    await page.click('button.login-btn');

    // Wait for redirect to home.html (simulated FB home)
    await page.waitForURL(/home\.html/, { timeout: 5000 });
    await page.waitForLoadState('networkidle');

    // Screenshot after login
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_home.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_home.png'));

    // --- SCHEDULE POST FLOW ---
    // Wait for post composer (simulate: look for textarea or input for posting)
    // Try common selectors for a post box
    let postBoxSelector = 'textarea, [contenteditable="true"], input[placeholder*="What\'s on your mind"], input[placeholder*="post"]';
    await page.waitForSelector(postBoxSelector, { timeout: 5000 });

    // Find the post box
    const postBox = await page.$(postBoxSelector);

    // Type a post
    const postContent = 'This is a scheduled post demo at ' + new Date().toLocaleString();
    await postBox.focus();
    await postBox.type(postContent);

    // Screenshot after typing post
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_post_composed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_post_composed.png'));

    // Look for "Schedule" or "Post Later" button, or a menu to schedule
    // Try several strategies: text, button, aria-label, etc.
    let scheduleButton = await page.$('button:has-text("Schedule")');
    if (!scheduleButton) {
      scheduleButton = await page.$('button:has-text("Post Later")');
    }
    if (!scheduleButton) {
      // Try a dropdown or menu
      const menuButton = await page.$('button:has-text("More")');
      if (menuButton) {
        await menuButton.click();
        await page.waitForTimeout(500); // wait for menu
        scheduleButton = await page.$('button:has-text("Schedule")');
      }
    }

    if (!scheduleButton) {
      // Try to find a generic schedule option
      scheduleButton = await page.$('text=Schedule');
    }

    if (!scheduleButton) {
      throw new Error('Could not find a "Schedule" or "Post Later" button on the page.');
    }

    // Click the schedule button
    await scheduleButton.click();

    // Wait for schedule dialog/modal to appear (look for date/time pickers)
    let dateInput = await page.$('input[type="date"], input[placeholder*="Date"], input[aria-label*="Date"]');
    let timeInput = await page.$('input[type="time"], input[placeholder*="Time"], input[aria-label*="Time"]');
    if (!dateInput) {
      // Try to find by label
      dateInput = await page.$('label:has-text("Date") >> input');
    }
    if (!timeInput) {
      timeInput = await page.$('label:has-text("Time") >> input');
    }

    // Fill in date/time for scheduling (schedule 1 hour in the future)
    const now = new Date();
    const future = new Date(now.getTime() + 60 * 60 * 1000);
    const yyyy = future.getFullYear();
    const mm = String(future.getMonth() + 1).padStart(2, '0');
    const dd = String(future.getDate()).padStart(2, '0');
    const hh = String(future.getHours()).padStart(2, '0');
    const min = String(future.getMinutes()).padStart(2, '0');

    if (dateInput) {
      await dateInput.fill(`${yyyy}-${mm}-${dd}`);
    }
    if (timeInput) {
      await timeInput.fill(`${hh}:${min}`);
    }

    // Screenshot after filling schedule
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_schedule_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_schedule_filled.png'));

    // Click "Schedule" or "Confirm" in the dialog
    let confirmButton = await page.$('button:has-text("Schedule")');
    if (!confirmButton) {
      confirmButton = await page.$('button:has-text("Confirm")');
    }
    if (!confirmButton) {
      confirmButton = await page.$('button:has-text("Done")');
    }
    if (!confirmButton) {
      throw new Error('Could not find a "Schedule" or "Confirm" button in the scheduling dialog.');
    }
    await confirmButton.click();

    // Wait for confirmation or for post to appear in "Scheduled Posts"
    // Try to find a confirmation message or a "Scheduled Posts" section
    let confirmationText = '';
    try {
      await page.waitForSelector('text=Your post is scheduled', { timeout: 4000 });
      confirmationText = 'Your post is scheduled';
    } catch {
      // Try to find a scheduled post in a list
      const scheduledPost = await page.$(`text=${postContent}`);
      if (scheduledPost) {
        confirmationText = 'Scheduled post appears in the list';
      }
    }

    // Screenshot after scheduling
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_scheduled_confirmation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_scheduled_confirmation.png'));

    // Extract scheduled post data for verification
    let scheduledPostText = '';
    const scheduledPostElement = await page.$(`text=${postContent}`);
    if (scheduledPostElement) {
      scheduledPostText = await scheduledPostElement.textContent();
    }

    return {
      success: true,
      action: "Scheduled a single post for later on Facebook demo site",
      extractedData: {
        scheduledPostText: scheduledPostText || null,
        confirmationText: confirmationText || null,
        scheduledFor: dateInput && timeInput ? `${yyyy}-${mm}-${dd} ${hh}:${min}` : null
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

