import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    // Step 1: Initial screenshot of login page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 2: Log in using form
    // Use placeholder attribute and type selector for email and password
    await page.waitForSelector('input[placeholder="Email or phone number"]');
    await page.fill('input[placeholder="Email or phone number"]', 'testuser@example.com');
    await page.waitForSelector('input[placeholder="Password"]');
    await page.fill('input[placeholder="Password"]', 'testpassword123');

    // Take screenshot before login
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_login.png'));

    // Click the login button using button text as selector strategy
    await page.waitForSelector('button.login-btn');
    await page.click('button.login-btn');

    // Wait for redirect to home.html (simulate post-login)
    await page.waitForTimeout(1500); // Wait for the JS setTimeout redirect
    await page.waitForLoadState('networkidle');
    // Wait for an element unique to home page (simulate) or fallback to url test
    let atHome = false;
    try {
      await page.waitForSelector('text=Create Post', { timeout: 2000 });
      atHome = true;
    } catch {}

    // Take screenshot after login
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_login.png'));

    // Step 3: Locate and click "Create Post" button (simulate opening post dialog)
    // Try multiple selectors since we don't know structure
    const createPostSelectors = [
      'button:has-text("Create Post")',
      'a:has-text("Create Post")',
      'text=Create Post'
    ];
    let foundCreatePost = false;
    for (const sel of createPostSelectors) {
      if (await page.$(sel)) {
        await page.click(sel);
        foundCreatePost = true;
        break;
      }
    }
    if (!foundCreatePost) {
      throw new Error('Could not find Create Post button');
    }

    // Wait for post dialog to appear
    await page.waitForTimeout(1000); // Simulate dialog animation
    // Look for textarea or input for post content
    const postInputSelectors = [
      'textarea[placeholder*="What\'s on your mind"]',
      'textarea',
      'input[placeholder*="What\'s on your mind"]',
      'input[type="text"]'
    ];
    let foundPostInput = false;
    for (const sel of postInputSelectors) {
      if (await page.$(sel)) {
        await page.fill(sel, 'Scheduled post test - demo automation');
        foundPostInput = true;
        break;
      }
    }
    if (!foundPostInput) {
      throw new Error('Could not find post input field');
    }

    // Screenshot after filling post
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_filled_post.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_filled_post.png'));

    // Step 4: Click "Schedule" or "Schedule Post" button (simulate scheduling for later)
    const scheduleSelectors = [
      'button:has-text("Schedule")',
      'button:has-text("Schedule Post")',
      'text=Schedule'
    ];
    let foundSchedule = false;
    for (const sel of scheduleSelectors) {
      if (await page.$(sel)) {
        await page.click(sel);
        foundSchedule = true;
        break;
      }
    }
    if (!foundSchedule) {
      throw new Error('Could not find Schedule button');
    }

    // Wait for scheduling options to appear and choose date/time (simulate)
    await page.waitForTimeout(500);

    // If there's a date picker, select a date in the future
    // Try generic selectors
    const dateInput = await page.$('input[type="date"]');
    if (dateInput) {
      // Pick tomorrow's date
      const tomorrow = new Date(Date.now() + 86400000);
      const yyyy = tomorrow.getFullYear();
      const mm = String(tomorrow.getMonth() + 1).padStart(2, '0');
      const dd = String(tomorrow.getDate()).padStart(2, '0');
      const futureDate = `${yyyy}-${mm}-${dd}`;
      await dateInput.fill(futureDate);
    }

    // Screenshot after selecting schedule
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_schedule_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_schedule_selected.png'));

    // Click final "Schedule" or "Confirm" button to set the schedule
    const confirmSelectors = [
      'button:has-text("Confirm")',
      'button:has-text("Schedule")',
      'button:has-text("Done")',
      'text=Confirm'
    ];
    for (const sel of confirmSelectors) {
      if (await page.$(sel)) {
        await page.click(sel);
        break;
      }
    }

    // Wait for confirmation (simulate)
    await page.waitForTimeout(1000);

    // Screenshot after scheduling
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_after_schedule.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_after_schedule.png'));

    // Extract confirmation message or look for scheduled post in UI
    // Try to find a toast, alert or scheduled post text
    let confirmationText = '';
    const confirmationSelectors = [
      '.toast, .alert-success, .notification', 
      'text=Your post has been scheduled',
      'text=Scheduled'
    ];
    for (const sel of confirmationSelectors) {
      const el = await page.$(sel);
      if (el) {
        confirmationText = await el.textContent();
        break;
      }
    }
    // As fallback, scrape all visible text and search for "scheduled"
    if (!confirmationText) {
      const bodyText = await page.textContent('body');
      if (bodyText && bodyText.toLowerCase().includes('scheduled')) {
        confirmationText = bodyText.match(/scheduled[^.!\n]*/i)?.[0] || '';
      }
    }

    return {
      success: true,
      action: "Scheduled a single Facebook post for later using alternative selectors and simulated UI flow.",
      extractedData: {
        confirmationText: confirmationText ? confirmationText.trim() : null
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
