  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // 1. Log in to Facebook demo
    await page.waitForSelector('#email');
    await page.fill('#email', 'storyuser@example.com');
    await page.fill('#password', 'testpassword');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_login.png'));

    await page.click('button.login-btn');
    // Wait for redirect to home.html (demo logic)
    await page.waitForTimeout(1200); // Give time for redirect

    // Wait for navigation to home.html (simulate, as we don't know the exact structure)
    await page.waitForLoadState('networkidle');
    // Wait for a known element on home.html (simulate Stories section)
    // Try to find a "Create Story" button or similar
    let storyButton = null;
    try {
      storyButton = await page.waitForSelector('button:has-text("Create Story")', { timeout: 3000 });
    } catch {
      // Try alternative selector (Facebook stories often use a plus icon or similar)
      storyButton = await page.$('text=Story') || await page.$('button[aria-label*="Story"]');
    }

    // If not found, fail gracefully
    if (!storyButton) {
      throw new Error('Could not find Create Story button or Story section');
    }

    // Click "Create Story"
    await storyButton.click();
    await page.waitForTimeout(1000); // Wait for modal or story upload UI

    // Wait for file input (simulate, as we don't know the exact selector)
    let fileInput = null;
    try {
      fileInput = await page.waitForSelector('input[type="file"]', { timeout: 3000 });
    } catch {
      // Try alternative: look for label or button to upload
      const uploadLabel = await page.$('label:has-text("Photo")') || await page.$('label:has-text("Upload")');
      if (uploadLabel) {
        await uploadLabel.click();
        fileInput = await page.waitForSelector('input[type="file"]', { timeout: 3000 });
      }
    }

    if (!fileInput) {
      throw new Error('Could not find file input for story upload');
    }

    // Upload a sample image (use a sample image from the test directory)
    const storyImagePath = path.join(__dirname, 'sample_story.jpg');
    await fileInput.setInputFiles(storyImagePath);

    await page.waitForTimeout(1000); // Wait for image preview

    // Add text overlay to the story
    // Try to find a text input or "Add Text" button
    let addTextBtn = null;
    try {
      addTextBtn = await page.waitForSelector('button:has-text("Text")', { timeout: 2000 });
    } catch {
      addTextBtn = await page.$('button:has-text("Aa")') || await page.$('button[aria-label*="Text"]');
    }
    if (addTextBtn) {
      await addTextBtn.click();
      await page.waitForTimeout(500);
    }

    // Find the text input for overlay
    let textInput = null;
    try {
      textInput = await page.waitForSelector('textarea', { timeout: 2000 });
    } catch {
      textInput = await page.$('input[type="text"]');
    }
    if (!textInput) {
      throw new Error('Could not find text overlay input');
    }

    // Type overlay text
    const overlayText = 'Hello from Playwright!';
    await textInput.fill(overlayText);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_story_with_text.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_story_with_text.png'));

    // Click "Share to Story" or similar button
    let shareBtn = null;
    try {
      shareBtn = await page.waitForSelector('button:has-text("Share to Story")', { timeout: 2000 });
    } catch {
      shareBtn = await page.$('button:has-text("Share")') || await page.$('button:has-text("Post")');
    }
    if (!shareBtn) {
      throw new Error('Could not find Share/Publish Story button');
    }
    await shareBtn.click();

    // Wait for confirmation or story to appear in list
    await page.waitForTimeout(1500);
    // Look for the story preview or confirmation message
    let storyPreview = null;
    try {
      storyPreview = await page.waitForSelector(`text=${overlayText}`, { timeout: 4000 });
    } catch {
      // Try to find an image thumbnail or "Your Story" label
      storyPreview = await page.$('text=Your Story') || await page.$('img[alt*="story"]');
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_story_uploaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_story_uploaded.png'));

    // Extract data for verification
    const extractedData = {};
    if (storyPreview) {
      extractedData.storyText = overlayText;
      extractedData.storyUploaded = true;
    } else {
      extractedData.storyUploaded = false;
    }

    return {
      success: extractedData.storyUploaded,
      action: "Uploaded a story with text overlay on Facebook demo",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

