    await page.waitForSelector(emailSelector, { timeout: 5000 });
    await page.fill(emailSelector, 'demo_user@example.com');

    // Wait for 'Password' input by placeholder, fallback to id
    const passwordSelector = 'input[placeholder="Password"], #password';
    await page.waitForSelector(passwordSelector, { timeout: 5000 });
    await page.fill(passwordSelector, 'demo_password123');

