    await page.waitForSelector(loginButtonSelector, { timeout: 5000 });
    await page.click(loginButtonSelector);

    // Wait for the simulated redirect to home.html (demo script waits 1s then redirects)
    await page.waitForTimeout(1500);
    await page.waitForLoadState('domcontentloaded');
    // Check navigation to home.html
    if (!page.url().toLowerCase().includes('home.html')) {
      throw new Error('Did not navigate to home.html after login');
    }

