    // Since HTML structure for home.html is not given, attempt common strategies.

    // Try various selectors for "Create Story"
    let storyButtonSelector = [
      'button:has-text("Story")',
      'a:has-text("Story")',
      'button:has-text("Create")',
      '[data-action="create-story"]',
      '[aria-label*="Story"]'
    ];
    let foundStoryBtn = null;
