      if (storyBtnXpath) {
        await storyBtnXpath.click();
      } else {
        // If still not found, simulate opening a story upload modal by keyboard shortcut (if any)
        // Otherwise, throw error but continue as much as possible
        throw new Error('Could not find "Create Story" button or link on home.html');
      }
    } else {
      await page.click(foundStoryBtn);
    }

    // Wait for a file input or modal to appear for story upload
    // Try common selectors for file input
    const fileInputSelectors = [
      'input[type="file"]',
      'input.accept-image',
      'input[aria-label*="photo"]'
    ];
    let foundFileInput = null;
    for (let sel of fileInputSelectors) {
      const inputHandle = await page.$(sel);
      if (inputHandle) {
        foundFileInput = sel;
        break;
      }
    }

    if (!foundFileInput) {
      // If no file input, simulate drag-and-drop or fallback to skipping file upload
      // For demo, create a fake image buffer and try to upload if possible
      throw new Error('Could not find file input to upload story image');
    } else {
      // Prepare a test image file (use a placeholder PNG from the internet or local path as needed)
      // For demo, use a generated buffer or a placeholder file (assume there's a test image in screenshotsDir)
      const testImagePath = path.join(screenshotsDir, 'test_story_image.png');
      // If the image doesn't exist, create a minimal PNG file
      const fs = await import('fs');
      if (!fs.existsSync(testImagePath)) {
        // Write a tiny white PNG
        const pngBase64 = "iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGNgYAAAAAMAASsJTYQAAAAASUVORK5CYII=";
        fs.writeFileSync(testImagePath, Buffer.from(pngBase64, 'base64'));
      }
      await page.setInputFiles(foundFileInput, testImagePath);
    }

    // Wait for image preview or "Next" button to appear (simulate overlay step)
    // Try to find a next/continue button
    const nextBtnSelectors = [
      'button:has-text("Next")',
      'button:has-text("Continue")',
      'button:has-text("Done")',
      '[aria-label*="Next"]'
    ];
    let foundNextBtn = null;
    for (let sel of nextBtnSelectors) {
      const btn = await page.$(sel);
      if (btn) {
        foundNextBtn = sel;
        break;
      }
    }

    if (foundNextBtn) {
      await page.click(foundNextBtn);
    } else {
      // If not found, try to continue anyway
      await page.waitForTimeout(500);
    }

    // Add text overlay: try to find input or textarea for overlay text
    const overlayInputSelectors = [
      'input[placeholder*="Text"]',
      'textarea[placeholder*="Text"]',
      'input[aria-label*="Text"]',
      'textarea[aria-label*="Text"]',
      'input[type="text"]',
      'textarea'
    ];
    let foundOverlayInput = null;
    for (let sel of overlayInputSelectors) {
      const input = await page.$(sel);
      if (input) {
        foundOverlayInput = sel;
        break;
      }
    }

    if (foundOverlayInput) {
      await page.fill(foundOverlayInput, 'Hello world! This is my story overlay.');
    } else {
      // If not found, skip overlay text
      throw new Error('Could not find overlay text input for story');
    }

    // Finalize story upload (try to click "Share", "Post", "Upload", etc.)
    const shareBtnSelectors = [
      'button:has-text("Share")',
      'button:has-text("Post")',
      'button:has-text("Upload")',
      'button:has-text("Add to Story")',
      '[aria-label*="Share"]'
    ];
    let foundShareBtn = null;
    for (let sel of shareBtnSelectors) {
      const btn = await page.$(sel);
      if (btn) {
        foundShareBtn = sel;
        break;
      }
    }

    if (foundShareBtn) {
      await page.click(foundShareBtn);
    } else {
      // Try to press Enter in overlay input as fallback
      if (foundOverlayInput) {
        await page.press(foundOverlayInput, 'Enter');
      }
    }

    // Wait for confirmation message or appearance of the story in feed
    await page.waitForTimeout(1200);

    // Take screenshot after upload
    const step4Path = path.join(screenshotsDir, 'step_4_story_uploaded.png');
    await page.screenshot({ path: step4Path });
    screenshots.push(step4Path);

    // Try to extract confirmation or check if overlay text appears in DOM
    let overlayTextFound = false;
    let storyText = '';
    // Try to find "Hello world!" in DOM
    const storyTextHandles = await page.$$('div, span, p');
    for (const handle of storyTextHandles) {
      const text = (await handle.innerText()).trim();
      if (text.includes('Hello world!')) {
        overlayTextFound = true;
        storyText = text;
        break;
      }
    }

    return {
      success: overlayTextFound,
      action: "Uploaded a story with text overlay on the Facebook demo site (simulated via selectors and overlays)",
      extractedData: {
        overlayTextFound,
        storyText
      },
      screenshots,
      error: overlayTextFound ? null : "Overlay text not found after upload; possible UI difference or selector mismatch"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // --- Login to demo Facebook (using different selectors & strategies) ---
    // Wait for the login form using its aria-label and placeholder text (alternative to ID)
    await page.waitForSelector('input[placeholder="Email or phone number"]', { timeout: 5000 });
    await page.waitForSelector('input[placeholder="Password"]', { timeout: 5000 });

    // Fill login fields using placeholder (not just IDs)
    await page.fill('input[placeholder="Email or phone number"]', 'storyuploader@example.com');
    await page.fill('input[placeholder="Password"]', 'storypass123');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_login.png'));

    // Click the Log In button using button text as selector fallback
    const loginBtnSelector = 'button.login-btn, button:has-text("Log In")';
    await page.waitForSelector(loginBtnSelector, { timeout: 5000 });
    await page.click(loginBtnSelector);

    // Wait for navigation to home.html (simulate login redirect)
    await page.waitForURL(/home\.html$/, { timeout: 10000 });
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_login.png'));

    // --- Navigate to "Stories" area (assume button or link exists) ---
    // Try to find a "Create Story" button by text, role, or aria-label
    let storyBtnSelector = 'button:has-text("Create Story"), a:has-text("Create Story"), [aria-label*="Story"]';
    let storyBtn = await page.$(storyBtnSelector);

    // If not found, try a generic button (fallback for demo UI)
    if (!storyBtn) {
      const allButtons = await page.$$('button');
      for (const btn of allButtons) {
        const btnText = (await btn.textContent()) || '';
        if (/story/i.test(btnText)) {
          storyBtn = btn;
          break;
        }
      }
    }

    if (!storyBtn) {
      throw new Error('Create Story button not found');
    }

    await storyBtn.click();
    // Wait for file input or upload area to appear
    await page.waitForSelector('input[type="file"], [data-testid="story-upload"], .story-upload', { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_story_upload_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_story_upload_modal.png'));

    // --- Upload story file (simulate, use a test image from project assets) ---
    // Find a visible file input
    const fileInput = await page.$('input[type="file"]');
    if (!fileInput) {
      throw new Error('File input for story upload not found');
    }

    // Use a demo image from the screenshotsDir or assets
    const testImagePath = path.join(screenshotsDir, '..', 'test_assets', 'sample_story.jpg');
    await fileInput.setInputFiles(testImagePath);

    // Wait for the image to preview in the modal (simulate by waiting for <img> or thumbnail)
    await page.waitForSelector('img, .story-preview', { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_upload_preview.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_upload_preview.png'));

    // --- Add text overlay to the story ---
    // Try to find a text input or textarea for overlay text
    let overlayInput = await page.$('input[placeholder*="Text"], textarea[placeholder*="Text"], input[aria-label*="Text"], textarea[aria-label*="Text"]');
    if (!overlayInput) {
      // Fallback: find any input/textarea in the modal
      const modalInputs = await page.$$('.modal input, .modal textarea, input, textarea');
      overlayInput = modalInputs.length > 0 ? modalInputs[0] : null;
    }
    if (!overlayInput) {
      throw new Error('Could not find text overlay input for story');
    }

    await overlayInput.fill('This is my automated story! ðŸš€');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_text_overlay.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_text_overlay.png'));

    // --- Submit the story (look for "Share" or "Post" button) ---
    let submitBtn =
      (await page.$('button:has-text("Share")')) ||
      (await page.$('button:has-text("Post")')) ||
      (await page.$('button:has-text("Upload")')) ||
      (await page.$('button:has-text("Done")')) ||
      (await page.$('button[type="submit"]'));
    if (!submitBtn) {
      // Fallback: pick the last visible button in the modal
      const modalBtns = await page.$$('.modal button');
      submitBtn = modalBtns.length ? modalBtns[modalBtns.length - 1] : null;
    }
    if (!submitBtn) {
      throw new Error('Could not find button to submit story');
    }
    await submitBtn.click();

    // Wait for confirmation or for story to appear in the feed (look for toast, success, or thumbnail)
    await page.waitForTimeout(2000);
    // Try to detect success (e.g., toast message or new story in feed)
    let storySuccess = false;
    let confirmMsg = '';
    const toast = await page.$('[class*=toast], [role="alert"], .success-message');
    if (toast) {
      storySuccess = true;
      confirmMsg = (await toast.textContent()) || '';
    } else {
      // Look for the story thumbnail with overlay text in the feed (simulate by searching for the overlay text)
      const overlayText = await page.$(`text="This is my automated story! ðŸš€"`);
      if (overlayText) {
        storySuccess = true;
        confirmMsg = 'Story appears in feed with overlay text';
      }
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_7_story_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_7_story_posted.png'));

    return {
      success: storySuccess,
      action: "Uploaded a story with a text overlay using alternative selectors and verified success.",
      extractedData: {
        confirmation: confirmMsg
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
