import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the login form to be visible
    await page.waitForSelector('#email');
    await page.waitForSelector('#password');

    // Fill in the login form
    await page.fill('#email', 'your_email@example.com');
    await page.fill('#password', 'your_password');

    // Take a screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_form.png'));

    // Click the login button
    await page.click('button.login-btn');

    // Wait for navigation after login
    await page.waitForLoadState('networkidle');

    // Take a screenshot after login
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_login.png'));

    // Navigate to notification preferences (assuming the URL or selector is known)
    await page.goto('https://www.facebook.com/settings?tab=notifications');
    await page.waitForLoadState('networkidle');

    // Take a screenshot of the notification preferences page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_notification_preferences.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_notification_preferences.png'));

    // Your logic to update notification preferences goes here
    // For example, clicking checkboxes or toggles

    // Take a final screenshot after updating preferences
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_updated_preferences.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_updated_preferences.png'));

    return {
      success: true,
      action: "Updated notification preferences successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

