import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the email input field and fill it
    await page.waitForSelector('#email');
    await page.fill('#email', 'example@example.com');

    // Wait for the password input field and fill it
    await page.waitForSelector('#password');
    await page.fill('#password', 'password123');

    // Click the login button
    await page.waitForSelector('button.login-btn');
    await page.click('button.login-btn');

    // Wait for navigation and take a screenshot after logging in
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login.png'));

    // Verify if login was successful by checking for a specific element on the landing page
    const loginSuccess = await page.isVisible('selector_for_success_indicator'); // Replace with actual selector for success
    if (loginSuccess) {
      return {
        success: true,
        action: "User logged in successfully and reached the homepage",
        extractedData: { message: "Login successful" },
        screenshots,
        error: null
      };
    } else {
      return {
        success: false,
        action: "Login failed or user not redirected",
        extractedData: {},
        screenshots,
        error: "Login did not succeed"
      };
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
