import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post to load
    await page.waitForSelector('div[data-testid="post_message"]', { timeout: 5000 });

    // Add fake reactions to the first post
    const firstPostReactionButtonSelector = 'div[data-testid="UFI2ReactionsCount"]';
    await page.click(firstPostReactionButtonSelector);
    await page.waitForTimeout(1000); // Wait for the reaction options to appear

    const reactionOptionSelector = 'div[role="menuitem"]'; // Select the reaction options
    await page.click(reactionOptionSelector); // Click on the first reaction
    await page.waitForTimeout(1000); // Wait for the reaction to register

    // Take a screenshot after adding the reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_reaction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_reaction.png'));

    return {
      success: true,
      action: "Added fake reactions to the first post",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
