import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and type a name to search
    await page.waitForSelector('input[placeholder="Search Facebook"]');
    await page.fill('input[placeholder="Search Facebook"]', 'Friend Name'); // Replace 'Friend Name' with the actual name to search
    await page.keyboard.press('Enter'); // Simulate pressing Enter to search

    // Wait for the search results to load
    await page.waitForSelector('.search-results'); // Adjust selector based on actual search results container

    // Take a screenshot of the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Click on the first friend in the search results
    await page.waitForSelector('.friend-list-item'); // Adjust selector based on actual friend list item
    await page.click('.friend-list-item'); // Click on the first friend

    // Wait for the add friend button to be visible and click it
    await page.waitForSelector('button:contains("Add Friend")'); // Adjust selector based on actual button text
    await page.click('button:contains("Add Friend")');

    // Take a screenshot after adding the friend
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_friend_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_friend_added.png'));

    return {
      success: true,
      action: "Added friend from search results",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

