import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the "Create new account" link to open the signup modal
    await page.waitForSelector('a.create-btn');
    await page.click('a.create-btn');

    // Wait for the signup form to appear
    await page.waitForSelector('input[placeholder="First name"]');

    // Fill out the signup form with fake data
    await page.fill('input[placeholder="First name"]', 'John');
    await page.fill('input[placeholder="Last name"]', 'Doe');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');
    await page.fill('input[placeholder="New password"]', 'fakepassword123');

    // Select gender (female in this case)
    await page.click('input[type="radio"][value="female"]');

    // Select date of birth
    await page.selectOption('select:nth-of-type(1)', '1'); // January
    await page.selectOption('select:nth-of-type(2)', '15'); // 15th
    await page.selectOption('select:nth-of-type(3)', '1990'); // Year 1990

    // Submit the signup form
    await page.click('button:contains("Sign Up")');

    // Wait for potential confirmation or error message to appear
    await page.waitForLoadState('networkidle');
    await page.waitForSelector('.success-message, .error-message');

    // Take a screenshot of the resulting state
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_signup_result.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_signup_result.png'));

    // Extract any relevant data (e.g., confirmation message)
    const confirmationMessage = await page.textContent('.success-message') || await page.textContent('.error-message');

    return {
      success: true,
      action: "Successfully created a fake account",
      extractedData: { confirmationMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
