import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the login form to be visible
    await page.waitForSelector('form#loginForm');

    // Fill in the email and password fields
    await page.fill('input#email', 'your_email@example.com'); // Replace with actual email
    await page.fill('input#password', 'your_password'); // Replace with actual password

    // Take a screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_form.png'));

    // Click the login button
    await page.click('button.login-btn');

    // Wait for navigation or a specific element that indicates login success
    await page.waitForLoadState('networkidle');
    await page.waitForSelector('selector_for_success_element'); // Replace with actual selector for success

    // Take a screenshot after logging in
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_logged_in.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_logged_in.png'));

    return {
      success: true,
      action: "Successfully logged in and navigated to the marketplace.",
      extractedData: {
        // Extract any meaningful data here if needed
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Interact with the login form
    await page.waitForSelector('#email'); // wait for the email input
    await page.fill('#email', 'test@example.com'); // fill in email

    await page.waitForSelector('#password'); // wait for the password input
    await page.fill('#password', 'password123'); // fill in password

    await page.waitForSelector('button.login-btn'); // wait for the login button
    await page.click('button.login-btn'); // click the login button

    // Take a screenshot after login attempt
    await page.waitForTimeout(2000); // wait for a brief moment to ensure the action is processed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login.png'));

    // Verify if login was successful by checking for a specific element on the next page
    const loginSuccess = await page.isVisible('text=Home'); // checking for a home element to confirm login
    if (loginSuccess) {
      return {
        success: true,
        action: "Successfully logged in and navigated to the home page.",
        extractedData: { email: 'test@example.com', status: 'logged in' },
        screenshots,
        error: null
      };
    } else {
      return {
        success: false,
        action: "Login failed, home element not found.",
        extractedData: {},
        screenshots,
        error: "Login was not successful."
      };
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

