import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill in the sign-up form
    await page.waitForSelector('input[placeholder="First name"]');
    await page.fill('input[placeholder="First name"]', 'John');

    await page.waitForSelector('input[placeholder="Last name"]');
    await page.fill('input[placeholder="Last name"]', 'Doe');

    await page.waitForSelector('input[placeholder="Mobile number or email"]');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');

    await page.waitForSelector('input[placeholder="New password"]');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select gender
    await page.check('input[type="radio"][value="male"]'); // Assuming male is one of the options

    // Select date of birth
    await page.selectOption('select:nth-of-type(1)', '1'); // January
    await page.selectOption('select:nth-of-type(2)', '15'); // 15th
    await page.selectOption('select:nth-of-type(3)', '1990'); // Year 1990

    // Submit the sign-up form
    await page.click('button:contains("Sign Up")');
    await page.waitForLoadState('networkidle');

    // Take a screenshot after form submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_signup.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_signup.png'));

    // Verify if the sign-up was successful by checking for a specific element
    const successMessage = await page.waitForSelector('text=Check your email for a confirmation link', { timeout: 5000 });
    const extractedData = {
      message: await successMessage.textContent()
    };

    return {
      success: true,
      action: "Created a fake marketplace listing",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

