import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and type the friend's name
    await page.waitForSelector('input[placeholder="Search Facebook"]');
    await page.fill('input[placeholder="Search Facebook"]', 'Friend Name');

    // Wait for the search results to load
    await page.waitForSelector('div[role="search"]');

    // Click on the first friend suggestion
    await page.click('div[role="search"] div:first-child');

    // Wait for the add friend button to be visible and click it
    await page.waitForSelector('button:contains("Add Friend")');
    await page.click('button:contains("Add Friend")');

    // Take a screenshot after adding the friend
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_friend.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_friend.png'));

    return {
      success: true,
      action: "Added friend from search results",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

