import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on "Create new account" link to open the signup form
    await page.waitForSelector('a.create-btn');
    await page.click('a.create-btn');
    await page.waitForLoadState('networkidle');

    // Take screenshot of the signup modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_signup_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_signup_modal.png'));

    // Fill out the signup form with fake data
    await page.fill('input[placeholder="First name"]', 'John');
    await page.fill('input[placeholder="Last name"]', 'Doe');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select gender (assuming selecting the first radio button)
    await page.click('input[type="radio"]:nth-of-type(1)');

    // Select date of birth
    await page.selectOption('select:nth-of-type(1)', '1'); // January
    await page.selectOption('select:nth-of-type(2)', '15'); // Day 15
    await page.selectOption('select:nth-of-type(3)', '2000'); // Year 2000

    // Take screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_filled_form.png'));

    // Submit the signup form
    await page.click('button:contains("Sign Up")');
    await page.waitForLoadState('networkidle');

    // Take final screenshot to confirm account creation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_confirmation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_confirmation.png'));

    return {
      success: true,
      action: "Successfully created a fake account",
      extractedData: {
        email: 'john.doe@example.com',
        name: 'John Doe'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

