import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Log in to Facebook
    await page.waitForSelector('#email');
    await page.fill('#email', 'your_email@example.com'); // Replace with your email
    await page.fill('#password', 'your_password'); // Replace with your password
    await page.click('button.login-btn');
    await page.waitForLoadState('networkidle');

    // Take screenshot after login
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login.png'));

    // Navigate to the story upload section (assuming it's accessible after login)
    // This part may vary based on the actual navigation flow
    await page.waitForSelector('selector_for_story_upload'); // Replace with actual selector
    await page.click('selector_for_story_upload'); // Replace with actual selector

    // Upload story with text overlay
    await page.waitForSelector('input[type="file"]'); // Wait for the file input
    const filePath = 'path_to_your_story_image.jpg'; // Replace with the path to your image
    await page.setInputFiles('input[type="file"]', filePath);

    // Wait for the text overlay input to be available
    await page.waitForSelector('input[placeholder="Add a caption..."]'); // Replace with actual selector
    await page.fill('input[placeholder="Add a caption..."]', 'Your overlay text here'); // Replace with your text

    // Submit the story
    await page.click('button.submit_story'); // Replace with actual selector for submit button
    await page.waitForLoadState('networkidle');

    // Take screenshot after story upload
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_story_uploaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_story_uploaded.png'));

    return {
      success: true,
      action: "Uploaded story with text overlay",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

