import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the login form to be visible
    await page.waitForSelector('form#loginForm', { timeout: 5000 });

    // Fill out the email and password fields
    await page.fill('input[placeholder="Email or phone number"]', 'your_email@example.com');
    await page.fill('input[placeholder="Password"]', 'your_password');

    // Click the Log In button
    await page.click('button.login-btn');

    // Wait for navigation after login
    await page.waitForLoadState('networkidle');

    // Take a screenshot after logging in
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login.png'));

    // Check if we are redirected to the home page
    const isLoggedIn = await page.isVisible('selector-for-home-page-element'); // Adjust selector accordingly

    return {
      success: true,
      action: isLoggedIn ? "Logged in successfully and navigated to home page." : "Login process completed but did not navigate to home page.",
      extractedData: { loggedIn: isLoggedIn },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
