import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and type the friend's name
    await page.waitForSelector('input[name="search"]'); // Adjust selector as necessary
    await page.fill('input[name="search"]', 'Friend\'s Name'); // Replace with actual friend's name
    await page.keyboard.press('Enter');

    // Wait for search results to load
    await page.waitForSelector('.search-results'); // Adjust selector as necessary

    // Take a screenshot of the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Click the "Add Friend" button for the first result
    await page.waitForSelector('.add-friend-button'); // Adjust selector as necessary
    await page.click('.add-friend-button');

    // Take a screenshot after adding the friend
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_friend_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_friend_added.png'));

    return {
      success: true,
      action: "Added friend from search results",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

