import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the comments section to load
    await page.waitForSelector('.comments-section');

    // Filter and delete spam comments
    const spamComments = await page.$$eval('.comment', comments => {
      return comments.filter(comment => {
        const text = comment.textContent.toLowerCase();
        return text.includes('spam') || text.includes('buy now') || text.includes('click here');
      });
    });

    for (const comment of spamComments) {
      const deleteButton = await comment.$('button.delete'); // Assuming there's a delete button
      if (deleteButton) {
        await deleteButton.click();
        await page.waitForTimeout(500); // Wait for the deletion to complete
      }
    }

    // Take a screenshot after deletion
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_deletion.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_deletion.png'));

    return {
      success: true,
      action: "Filtered and deleted spam comments",
      extractedData: { deletedCommentsCount: spamComments.length },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

