import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill in the sign-up form
    await page.waitForSelector('input[placeholder="First name"]');
    await page.fill('input[placeholder="First name"]', 'John');

    await page.waitForSelector('input[placeholder="Last name"]');
    await page.fill('input[placeholder="Last name"]', 'Doe');

    await page.waitForSelector('input[placeholder="Mobile number or email"]');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');

    await page.waitForSelector('input[placeholder="New password"]');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select gender
    await page.check('input[type="radio"][value="male"]'); // Assuming 'male' is one of the gender options

    // Submit the sign-up form
    await page.waitForSelector('button:contains("Sign Up")');
    await page.click('button:contains("Sign Up")');

    // Wait for the next page to load
    await page.waitForLoadState('networkidle');

    // Take a screenshot after form submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_signup.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_signup.png'));

    // Extract meaningful data to verify task completion
    const confirmationMessage = await page.textContent('h1'); // Assuming there's a confirmation message in an <h1> tag
    const extractedData = {
      confirmationMessage
    };

    return {
      success: true,
      action: "Created a fake marketplace listing",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

