import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and type the friend's name
    await page.waitForSelector('input[name="search"]'); // Adjust selector as needed
    await page.fill('input[name="search"]', 'Friend Name'); // Replace 'Friend Name' with the actual name

    // Wait for the search results to load
    await page.waitForSelector('.search-results'); // Adjust selector as needed

    // Take a screenshot of the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Click on the first friend in the search results
    await page.click('.search-results .friend-item'); // Adjust selector as needed

    // Wait for the add friend button to be visible
    await page.waitForSelector('button.add-friend'); // Adjust selector as needed

    // Click the add friend button
    await page.click('button.add-friend'); // Adjust selector as needed

    // Take a screenshot after adding the friend
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_friend_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_friend_added.png'));

    return {
      success: true,
      action: "Added friend from search results",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

