import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Interact with the login form
    await page.waitForSelector('#email');
    await page.fill('#email', 'your_email@example.com'); // Replace with actual email
    await page.waitForSelector('#password');
    await page.fill('#password', 'your_password'); // Replace with actual password

    // Click the login button
    await page.waitForSelector('button.login-btn');
    await page.click('button.login-btn');

    // Wait for navigation or some indication of successful login
    await page.waitForLoadState('networkidle');

    // Take screenshot after login attempt
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login.png'));

    // Verify successful login by checking for a specific element on the homepage
    const isLoggedIn = await page.isVisible('selector_for_logged_in_element'); // Replace with actual selector
    const extractedData = isLoggedIn ? { message: "Login successful" } : { message: "Login failed" };

    return {
      success: isLoggedIn,
      action: isLoggedIn ? "Logged in successfully" : "Login failed",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

