import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the "Create new account" link to open the signup modal
    await page.waitForSelector('a.create-btn');
    await page.click('a.create-btn');
    await page.waitForSelector('input[placeholder="First name"]');

    // Fill out the signup form
    await page.fill('input[placeholder="First name"]', 'John');
    await page.fill('input[placeholder="Last name"]', 'Doe');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select gender (assuming we select the first radio button)
    await page.click('input[type="radio"]:nth-of-type(1)');

    // Select date of birth
    await page.selectOption('select:nth-of-type(1)', '5'); // May
    await page.selectOption('select:nth-of-type(2)', '15'); // 15th
    await page.selectOption('select:nth-of-type(3)', '1990'); // Year 1990

    // Take a screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_form.png'));

    // Submit the signup form
    await page.click('button:contains("Sign Up")');
    await page.waitForLoadState('networkidle');

    // Take a screenshot after form submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_submission.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_submission.png'));

    // Extract meaningful data to verify task completion
    const successMessage = await page.locator('text=Check your email').isVisible();

    return {
      success: true,
      action: "Successfully created a fake account",
      extractedData: { successMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

