    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the "Create new account" link to open the signup modal
    await page.waitForSelector('a.create-btn');
    await page.click('a.create-btn');

    // Wait for the signup form to be visible
    await page.waitForSelector('form[method="POST"]');

    // Fill in the signup form with fake data
    await page.fill('input[placeholder="First name"]', 'John');
    await page.fill('input[placeholder="Last name"]', 'Doe');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select a random gender (e.g., Male)
    await page.check('input[type="radio"][value="male"]');

    // Select a birth date
    await page.selectOption('select:nth-of-type(1)', '1'); // Month - January
    await page.selectOption('select:nth-of-type(2)', '15'); // Day - 15
    await page.selectOption('select:nth-of-type(3)', '2000'); // Year - 2000

    // Take a screenshot of the filled signup form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_form.png'));

    // Submit the signup form
    await page.click('button:contains("Sign Up")');

    // Wait for a success confirmation or error message
    await page.waitForTimeout(2000); // Adjust as necessary for loading
    const successMessage = await page.locator('text="Confirmation message or similar text"').count() > 0;

    // Take a final screenshot after the submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_submission.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_submission.png'));

    return {
      success: successMessage,
      action: "Fake account created successfully",
      extractedData: {
        email: 'john.doe@example.com'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

