import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill in the sign-up form
    await page.waitForSelector('input[placeholder="First name"]');
    await page.fill('input[placeholder="First name"]', 'John');

    await page.waitForSelector('input[placeholder="Last name"]');
    await page.fill('input[placeholder="Last name"]', 'Doe');

    await page.waitForSelector('input[placeholder="Mobile number or email"]');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');

    await page.waitForSelector('input[placeholder="New password"]');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select gender
    await page.waitForSelector('input[type="radio"][value="male"]');
    await page.check('input[type="radio"][value="male"]');

    // Select date of birth
    await page.selectOption('select:nth-of-type(1)', '1'); // Month
    await page.selectOption('select:nth-of-type(2)', '15'); // Day
    await page.selectOption('select:nth-of-type(3)', '1990'); // Year

    // Take a screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_form.png'));

    // Submit the sign-up form
    await page.waitForSelector('button:contains("Sign Up")');
    await page.click('button:contains("Sign Up")');

    // Wait for navigation after form submission
    await page.waitForLoadState('networkidle');

    // Take final screenshot after submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_submission.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_submission.png'));

    // Verify successful sign-up by checking for a specific element on the new page
    const successMessage = await page.isVisible('text=Welcome, John');

    return {
      success: true,
      action: "Successfully created an event invitation list",
      extractedData: { successMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

