export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Interact with the Sign Up form
    await page.waitForSelector('input[placeholder="First name"]');
    await page.fill('input[placeholder="First name"]', 'John');

    await page.waitForSelector('input[placeholder="Last name"]');
    await page.fill('input[placeholder="Last name"]', 'Doe');

    await page.waitForSelector('input[placeholder="Mobile number or email"]');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');

    await page.waitForSelector('input[placeholder="New password"]');
    await page.fill('input[placeholder="New password"]', 'Password123');

    // Select gender radio button (assuming the first radio button is for 'Male')
    await page.waitForSelector('input[type="radio"]');
    await page.click('input[type="radio"]:nth-of-type(1)'); // Select first radio button

    // Select birth date
    await page.selectOption('select:nth-of-type(1)', '5'); // Month: May
    await page.selectOption('select:nth-of-type(2)', '15'); // Day: 15
    await page.selectOption('select:nth-of-type(3)', '1990'); // Year: 1990

    // Click the Sign Up button
    await page.waitForSelector('button:contains("Sign Up")');
    await page.click('button:contains("Sign Up")');

    // Take a screenshot after form submission
    await page.waitForTimeout(2000); // Wait for potential loading
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_signup.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_signup.png'));

    // Verify successful sign-up by checking for an expected element (this might vary)
    const successMessage = await page.waitForSelector('div[class*="success-message"]', { timeout: 5000 });
    const extractedData = { message: await successMessage.innerText() };

    return {
      success: true,
      action: "Successfully created an account and extracted confirmation message",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
