import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the "Create new account" link to open the signup modal
    await page.waitForSelector('a.create-btn');
    await page.click('a.create-btn');

    // Wait for the signup form to appear
    await page.waitForSelector('input[placeholder="First name"]');

    // Fill in the signup form with fake information
    await page.fill('input[placeholder="First name"]', 'John');
    await page.fill('input[placeholder="Last name"]', 'Doe');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select gender (for example, Male)
    await page.click('input[type="radio"][value="male"]');

    // Select date of birth
    await page.selectOption('select:nth-of-type(1)', '5'); // May
    await page.selectOption('select:nth-of-type(2)', '15'); // 15th
    await page.selectOption('select:nth-of-type(3)', '1990'); // Year 1990

    // Submit the signup form
    await page.click('button:contains("Sign Up")');

    // Wait for a confirmation message or redirect
    await page.waitForSelector('div[role="alert"]', { timeout: 5000 });

    // Take a screenshot after signup
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_signup_complete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_signup_complete.png'));

    return {
      success: true,
      action: "Successfully created a fake account",
      extractedData: {
        email: 'john.doe@example.com',
        name: 'John Doe'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

