import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post to be visible
    await page.waitForSelector('div[data-testid="post_message"]', { timeout: 5000 });

    // Add fake reactions to the first post
    const firstPostReactionButton = await page.$('div[data-testid="post_reaction"]');
    if (firstPostReactionButton) {
      await firstPostReactionButton.click();
      await page.waitForTimeout(1000); // Wait for the reaction menu to appear

      // Choose a random reaction
      const reactions = await page.$$('div[data-testid="reaction"]');
      if (reactions.length > 0) {
        await reactions[Math.floor(Math.random() * reactions.length)].click();
      }
    }

    // Take a screenshot after adding reactions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    return {
      success: true,
      action: "Added fake reactions to the first post",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

