import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the "Create new account" link to open the signup modal
    await page.waitForSelector('a.create-btn');
    await page.click('a.create-btn');
    await page.waitForLoadState('networkidle');

    // Take screenshot of the signup modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_signup_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_signup_modal.png'));

    // Fill in the signup form with fake data
    await page.fill('input[placeholder="First name"]', 'John');
    await page.fill('input[placeholder="Last name"]', 'Doe');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select gender
    await page.click('input[type="radio"][value="male"]'); // Assuming male is the first radio button

    // Select date of birth
    await page.selectOption('select:nth-of-type(1)', '1'); // January
    await page.selectOption('select:nth-of-type(2)', '15'); // Day 15
    await page.selectOption('select:nth-of-type(3)', '1990'); // Year 1990

    // Submit the signup form
    await page.click('button:contains("Sign Up")');
    await page.waitForLoadState('networkidle');

    // Take screenshot after form submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_signup.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_signup.png'));

    // Extract meaningful data to verify task completion
    const successMessage = await page.textContent('div.success-message'); // Adjust selector based on actual success message element
    const extractedData = {
      message: successMessage,
      email: 'john.doe@example.com'
    };

    return {
      success: true,
      action: "Created a fake account successfully",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

