import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the email input field and enter email
    await page.waitForSelector('#email', { timeout: 5000 });
    await page.fill('#email', 'test@example.com');

    // Wait for the password input field and enter password
    await page.waitForSelector('#password', { timeout: 5000 });
    await page.fill('#password', 'password123');

    // Click the login button
    await page.waitForSelector('button.login-btn', { timeout: 5000 });
    await page.click('button.login-btn');

    // Wait for navigation or a specific element that indicates login success
    await page.waitForSelector('div#loginSuccessMessage', { timeout: 5000 }); // Assuming this is a success message element

    // Take a screenshot after login
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login.png'));

    // Extracting meaningful data to verify task completion
    const successMessage = await page.textContent('div#loginSuccessMessage');

    return {
      success: true,
      action: "Successfully logged in to Facebook",
      extractedData: { message: successMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
