import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the login form to be visible
    await page.waitForSelector('#email');
    await page.waitForSelector('#password');

    // Fill in the login form
    await page.fill('#email', 'test@example.com');
    await page.fill('#password', 'password123');

    // Take screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_form.png'));

    // Click the login button
    await page.click('button.login-btn');

    // Wait for the next page to load
    await page.waitForLoadState('networkidle');

    // Take screenshot after login
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_login.png'));

    // Extract meaningful data to verify task completion
    const userLoggedIn = await page.isVisible('selector-for-logged-in-user'); // Replace with actual selector
    const extractedData = {
      loggedIn: userLoggedIn,
      timestamp: new Date().toISOString()
    };

    return {
      success: true,
      action: "User logged in successfully and verified.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

