import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for email input field and fill it
    await page.waitForSelector('#email');
    await page.fill('#email', 'test@example.com');

    // Wait for password input field and fill it
    await page.waitForSelector('#password');
    await page.fill('#password', 'examplePassword');

    // Click on the Log In button
    await page.waitForSelector('button.login-btn');
    await page.click('button.login-btn');

    // Wait for navigation or content change after login
    await page.waitForLoadState('networkidle');

    // Take screenshot after login
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login.png'));

    // Extract meaningful data to verify successful login
    const successMessage = await page.locator('text=Welcome').innerText();

    return {
      success: true,
      action: "Successfully logged in and navigated to the welcome page.",
      extractedData: { message: successMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
