import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the login form to be visible
    await page.waitForSelector('#email');
    await page.waitForSelector('#password');

    // Input email and password
    await page.fill('#email', 'suspicious@example.com'); // Enter a suspicious email
    await page.fill('#password', 'suspiciousPassword123'); // Enter a suspicious password

    // Click the login button
    await page.click('button.login-btn');

    // Wait for the response or any indication of success/failure
    await page.waitForLoadState('networkidle');

    // Take a screenshot after attempting to log in
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login_attempt.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login_attempt.png'));

    // Extract any meaningful data to verify task completion
    const errorMessage = await page.locator('#errorMessage').innerText();

    return {
      success: true,
      action: "Posted a message with a suspicious link",
      extractedData: { errorMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

