import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the login form to be visible
    await page.waitForSelector('#email');
    await page.waitForSelector('#password');

    // Fill in the login form
    await page.fill('#email', 'example@example.com');
    await page.fill('#password', 'password123');

    // Click the login button
    await Promise.all([
      page.click('button.login-btn'),
      page.waitForLoadState('networkidle')
    ]);

    // Take a screenshot after login attempt
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login.png'));

    // Check if login was successful by looking for a specific element on the home page
    const loginSuccess = await page.isVisible('text=Home');

    return {
      success: loginSuccess,
      action: loginSuccess ? "Successfully logged in" : "Login failed",
      extractedData: loginSuccess ? { message: "Logged in successfully" } : { message: "Login attempt failed" },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
