import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the "Create new account" link to open the signup modal
    await page.waitForSelector('a.create-btn');
    await page.click('a.create-btn');
    await page.waitForSelector('input[placeholder="First name"]');

    // Fill in the signup form with fake information
    await page.fill('input[placeholder="First name"]', 'John');
    await page.fill('input[placeholder="Last name"]', 'Doe');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select gender (assuming the first radio button is for Male)
    await page.click('input[type="radio"]:nth-of-type(1)');

    // Select date of birth
    await page.selectOption('select:nth-of-type(1)', '1'); // Month: January
    await page.selectOption('select:nth-of-type(2)', '15'); // Day: 15
    await page.selectOption('select:nth-of-type(3)', '1990'); // Year: 1990

    // Click the "Sign Up" button
    await page.click('button:contains("Sign Up")');
    await page.waitForLoadState('networkidle');

    // Take a screenshot after form submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_signup_submitted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_signup_submitted.png'));

    // Verify if the account creation was successful by checking for a specific element
    const successMessage = await page.$('text=Check your email for a confirmation link');
    const isSuccess = successMessage !== null;

    return {
      success: isSuccess,
      action: isSuccess ? "Fake account created successfully" : "Failed to create fake account",
      extractedData: {
        email: 'john.doe@example.com',
        name: 'John Doe'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

