import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the login form to be visible
    await page.waitForSelector('form#loginForm');

    // Fill in the email and password fields
    await page.fill('input[placeholder="Email or phone number"]', 'your_email@example.com');
    await page.fill('input[placeholder="Password"]', 'your_password');

    // Click the Log In button
    await Promise.all([
      page.waitForNavigation(),
      page.click('button.login-btn')
    ]);

    // Wait for the group post page to load
    await page.waitForSelector('div.group-post-input');

    // Take a screenshot after logging in
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_login.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_login.png'));

    // Fill in the post content
    await page.fill('div.group-post-input textarea', 'Your post content here');

    // Click the share button
    await Promise.all([
      page.waitForSelector('button.share-post-btn'),
      page.click('button.share-post-btn')
    ]);

    // Wait for confirmation of post sharing
    await page.waitForSelector('div.confirmation-message');

    // Take a final screenshot of the confirmation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_post_shared.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_post_shared.png'));

    return {
      success: true,
      action: "Successfully shared post to the specified group",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
