import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Interact with the notification preferences
    // Assuming we need to navigate to the settings page to update notification preferences
    await page.waitForSelector('a:contains("Settings")'); // Wait for the settings link to be visible
    await page.click('a:contains("Settings")'); // Click on the settings link
    await page.waitForLoadState('networkidle'); // Wait for the settings page to load

    // Take a screenshot of the settings page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_page.png'));

    // Update notification preferences
    await page.waitForSelector('input[name="notifications"]'); // Wait for the notifications input to be visible
    await page.click('input[name="notifications"]'); // Click to update notification preferences

    // Take a screenshot after updating preferences
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_updated_preferences.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_updated_preferences.png'));

    return {
      success: true,
      action: "Updated notification preferences successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

