import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill out the signup form
    await page.waitForSelector('input[placeholder="First name"]');
    await page.fill('input[placeholder="First name"]', 'John');

    await page.fill('input[placeholder="Last name"]', 'Doe');
    await page.fill('input[placeholder="Mobile number or email"]', 'john.doe@example.com');
    await page.fill('input[placeholder="New password"]', 'SecurePassword123');

    // Select gender
    await page.check('input[type="radio"][value="male"]'); // Assuming male is one of the options

    // Select date of birth
    await page.selectOption('select:nth-of-type(1)', '1'); // January
    await page.selectOption('select:nth-of-type(2)', '15'); // Day 15
    await page.selectOption('select:nth-of-type(3)', '1990'); // Year 1990

    // Submit the signup form
    await page.click('button:contains("Sign Up")');

    // Wait for a success message or redirection
    await page.waitForSelector('div.success-message', { timeout: 5000 }); // Adjust selector based on actual success message

    // Take a screenshot after form submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_signup.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_signup.png'));

    return {
      success: true,
      action: "Successfully created a fake marketplace listing",
      extractedData: {
        name: 'John Doe',
        email: 'john.doe@example.com'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

