import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the login form to be visible
    await page.waitForSelector('form#loginForm');

    // Fill in the email and password fields
    await page.fill('#email', 'your_email@example.com'); // Replace with actual email
    await page.fill('#password', 'your_password'); // Replace with actual password

    // Take a screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_form.png'));

    // Click the login button
    await page.click('button.login-btn');

    // Wait for navigation or specific element indicating successful login
    await page.waitForSelector('selector_for_successful_login', { timeout: 5000 }); // Replace with actual selector

    // Take a screenshot after logging in
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_logged_in.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_logged_in.png'));

    // Send birthday message
    await page.goto('https://www.facebook.com/messages'); // Navigate to messages
    await page.waitForSelector('selector_for_message_input'); // Replace with actual selector for message input
    await page.fill('selector_for_message_input', 'Happy Birthday, friend! ðŸŽ‰'); // Replace with actual selector
    await page.click('selector_for_send_button'); // Replace with actual selector for send button

    // Take a screenshot after sending the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_message_sent.png'));

    return {
      success: true,
      action: "Sent birthday message to friend",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

