import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Locate the search bar and input a friend's name
    await page.waitForSelector('input[placeholder="Search Facebook"]');
    await page.fill('input[placeholder="Search Facebook"]', 'Friend Name'); // Replace 'Friend Name' with the actual name

    // Wait for search results to load
    await page.waitForSelector('div[role="search"]'); // Adjust selector according to actual search result container

    // Click on the first result in the search results
    await page.waitForSelector('div[role="search"] div[data-testid="result"]', { timeout: 5000 });
    await page.click('div[role="search"] div[data-testid="result"]:first-child');

    // Wait for the friend's profile to load
    await page.waitForLoadState('domcontentloaded');

    // Click the 'Add Friend' button
    await page.waitForSelector('button[data-testid="add_friend_button"]');
    await page.click('button[data-testid="add_friend_button"]');

    // Take a screenshot after adding a friend
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_friend_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_friend_added.png'));

    return {
      success: true,
      action: "Added friend from search results",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
