import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // Wait for posts to be loaded
    // Posts are inside .posts > .post
    await page.waitForSelector('.posts .post', { timeout: 5000 });

    // Get all post elements
    const postHandles = await page.$$('.posts .post');

    const extractedData = [];

    for (let i = 0; i < postHandles.length; i++) {
      const post = postHandles[i];

      // Extract caption text
      let captionText = '';
      try {
        const captionHandle = await post.$('.post-caption .caption-text');
        if (captionHandle) {
          captionText = await captionHandle.innerText();
        }
      } catch (e) {
        captionText = '';
      }

      // Extract hashtags from caption
      // Hashtags are words starting with #
      let hashtags = [];
      if (captionText) {
        hashtags = Array.from(
          captionText.matchAll(/#\w+/g),
          m => m[0]
        );
      }

      // Extract like count
      let likeCount = null;
      try {
        // Try to find an element with class .post-likes
        const likesHandle = await post.$('.post-likes');
        if (likesHandle) {
          const likesText = await likesHandle.innerText();
          // Try to extract the number (e.g., "123 likes")
          const match = likesText.match(/(\d[\d,\.]*)/);
          if (match) {
            // Remove commas and parse as number
            likeCount = parseInt(match[1].replace(/,/g, ''), 10);
          } else {
            likeCount = likesText;
          }
        }
      } catch (e) {
        likeCount = null;
      }

      extractedData.push({
        postIndex: i + 1,
        caption: captionText,
        hashtags,
        likeCount
      });
    }

    // Take a screenshot after extraction
    const afterExtractScreenshot = path.join(screenshotsDir, 'step_2_after_extraction.png');
    await page.screenshot({ path: afterExtractScreenshot });
    screenshots.push(afterExtractScreenshot);

    // Log to console as required by the task
    // (This will show up in Playwright's console output)
    console.log('Extracted Instagram post data:', extractedData);

    return {
      success: true,
      action: "Extracted hashtags from all post captions and like counts, output to console",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for at least one post to appear using a different selector strategy
    // Instead of just class, use XPath to target the posts container and then .post elements
    await page.waitForSelector('//div[contains(@class, "posts")]/div[contains(@class, "post")]', { timeout: 5000 });

    // Take a screenshot after posts are loaded
    const postsLoadedShot = path.join(screenshotsDir, 'step_2_posts_loaded.png');
    await page.screenshot({ path: postsLoadedShot });
    screenshots.push(postsLoadedShot);

    // Extract all post nodes using XPath (alternative to basic class selector)
    const postHandles = await page.$$('xpath=//div[contains(@class,"posts")]/div[contains(@class,"post")]');
    const results = [];

    for (const postHandle of postHandles) {
      // Get caption text using a more robust selector: find any descendant with class containing 'caption-text'
      const captionEl = await postHandle.$('xpath=.//div[contains(@class,"post-caption")]//span[contains(@class,"caption-text")]');
      let captionText = '';
      if (captionEl) {
        captionText = await captionEl.innerText();
      }

      // Extract hashtags using RegExp
      const hashtags = [];
      if (captionText) {
        // Regex for #hashtags (letters, numbers, underscore)
        const hashtagMatches = captionText.match(/#[\w]+/g);
        if (hashtagMatches) {
          hashtags.push(...hashtagMatches);
        }
      }

      // Get like count using alternative strategy:
      // Find span with class including 'post-likes', or any element with text matching 'likes'
      let likeCountText = '';
      // Try span[class*="post-likes"]
      const likesEl = await postHandle.$('xpath=.//*[contains(@class,"post-likes") or contains(text(),"likes")]');
      if (likesEl) {
        likeCountText = await likesEl.innerText();
      }

      // Parse like count as number if possible
      let likeCount = null;
      if (likeCountText) {
        // e.g., "123 likes" or just "123"
        const likeNumMatch = likeCountText.match(/(\d[\d,\.]*)/);
        if (likeNumMatch) {
          likeCount = parseInt(likeNumMatch[1].replace(/,/g, ''));
        }
      }

      results.push({
        caption: captionText,
        hashtags,
        likeCount
      });
    }

    // Take a screenshot after extraction for verification
    const afterExtractShot = path.join(screenshotsDir, 'step_3_after_extraction.png');
    await page.screenshot({ path: afterExtractShot });
    screenshots.push(afterExtractShot);

    // Log the results to the console as requested
    // (for Playwright test logs only, not affecting return value)
    // eslint-disable-next-line no-console
    console.log('Extracted post data:', results);

    return {
      success: true,
      action: "Extracted hashtags from all post captions and like counts using XPath and advanced selectors",
      extractedData: { posts: results },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

