import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the messages button in the navigation
    await page.waitForSelector('.messages-btn');
    await page.click('.messages-btn');

    // Wait for the messages modal to open
    await page.waitForSelector('#messagesModal');

    // Take a screenshot of the messages modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_messages_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_messages_modal.png'));

    // Verify that the messages modal is visible
    const isModalVisible = await page.isVisible('#messagesModal');

    return {
      success: true,
      action: "Clicked messages button and verified messages modal opened",
      extractedData: { isModalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

