import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first suggestion user to appear
    await page.waitForSelector('.suggestion-item:first-child .follow-btn');

    // Click the follow button of the first user suggestion
    await page.click('.suggestion-item:first-child .follow-btn');

    // Take a screenshot after clicking the follow button
    await page.waitForTimeout(1000); // wait for any animation or state change
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Verify the button text has changed to "Following"
    const buttonText = await page.innerText('.suggestion-item:first-child .follow-btn');
    const isFollowing = buttonText === "Following";

    return {
      success: isFollowing,
      action: isFollowing ? "Successfully followed the first user in suggestions" : "Follow button did not change to 'Following'",
      extractedData: { buttonText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
