import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the first post's comment button
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
    await page.click('.post:first-child .action-btn[aria-label*="Comment"]');

    // Wait for the comment input to appear
    await page.waitForSelector('#commentInput');

    // Type the comment
    await page.fill('#commentInput', 'Great post!');

    // Click the comment button to submit
    await page.click('#postCommentBtn');

    // Wait for the comment to appear in the comments section
    await page.waitForSelector('.post:first-child .comments-section'); // Adjust selector as needed
    const comments = await page.$$eval('.post:first-child .comments-section .comment', elements => elements.map(el => el.textContent));

    // Take a screenshot after commenting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_comment.png'));

    // Verify the comment appears
    const commentExists = comments.includes('Great post!');

    return {
      success: commentExists,
      action: commentExists ? "Comment added successfully" : "Comment not found",
      extractedData: { comments },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

