    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post's comment button to be visible and click it
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
