    return {
      success: commentExists,
      action: "Comment 'Great post!' added to the first post",
      extractedData: { commentExists },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
